import functools
import json
import itertools
import docopt
import types
USAGE = """
    Usage:
    	sbg_pair_fastqs_by_metadata.py --fastq_list FILE... --in_metafile FILE --out_metafile FILE [options]

    Description:
        Tool accepts list of FATSQ files for one sample as the input and groups them into pairs
        (two files for each paired end). This grouping is done using metadata values that are creating
        unique combination for each pair or of FASTQ files. Metadata that fields that are uniquely defining
        one FASTQ pair are Sample ID, Library ID, Platform Unit ID and File Segment Number. Listed order of metadata fields is also
        representing their hierarchy in the metadata structure. Not all of these four metadata fields are required,
        but the present set has to be sufficient to create unique combinations for each pair of FASTQ files.

    Options:

        --help                                Show help dialog.

        --version                             Tool version.

        --fatsq_list FILE...                  List of the FASTQ files with properly set metadata fileds.

        --in_metafile FILE                    File from which necessary metadata information will be extracted.
                                              Expected value for the SBG platfrom is job.json. [Default: job.json]

        --out_metafile FILE                   File into which necessary file structure is going to be written.
                                              Expected value for the SBG platform is cwl.output.json.
                                              [Default: cwl.output.json]


"""

def make_rg_id(metadata_key, input1):

    input_meta = input1.values()[0]

    S = '__!__'
    rg = list()
    for key in ['sample_group', 'sample_id', 'library_id', 'platform_unit_id']:
        if key in input_meta:
            rg.append(input_meta[key])
        else:
            rg.append('')
    if 'file_segment_number' in input_meta and input_meta['file_segment_number'] is not None:
        rg.append(str(input_meta['file_segment_number']))
    else:
        rg.append('')

    rg_map = {
        'sample_id': rg[:2],
        'library_id': rg[:3],
        'platform_unit_id': rg[:4],
        'file_segment_number': rg[:5],
    }

    return S.join(rg_map[metadata_key]) if metadata_key in rg_map else getattr(input_meta, metadata_key)


def group_inputs(inp):

    metadata_key = 'file_segment_number'
    if str(metadata_key) == 'None':
        return {'': [f for f in inp]}
    if metadata_key == 'file':
        return {f: [f] for f in inp}
    key_getter = functools.partial(make_rg_id, metadata_key)
    print inp
    files = sorted([x for x in inp], key=key_getter)
    tuple_list_temp = [[f for f in val] for key, val in itertools.groupby(files, key_getter)]
    tuple_list=list()
    for elem in tuple_list_temp:
        for key in elem[0].keys():
            if len(files) != 1:
                if elem[0][key]['paired_end'] == "2":
                    tuple_list.append([str(elem[1].keys()[0]), str(elem[0].keys()[0])])
                else:
                    tuple_list.append([str(elem[0].keys()[0]), str(elem[1].keys()[0])])
            else:
                    tuple_list.append([str(elem[0].keys()[0])])

    print tuple_list
    return tuple_list

def main():

    args = docopt.docopt(USAGE, version = 1.0)

    job_json = args["--in_metafile"]
    job_json_file = open(job_json)
    job_json_str = job_json_file.read()
    job_json_dict = json.loads(job_json_str)
    fastq_list = job_json_dict['inputs']["fastq_list"]
    if isinstance(fastq_list, list):
        fastq_list = job_json_dict['inputs']["fastq_list"]
    else:
        fastq_list = list()
        fastq_list.append(job_json_dict['inputs']["fastq_list"])
    file_list = [{elem['path'] : elem['metadata']} for elem in fastq_list]
    tuple_list = group_inputs(file_list)

    for elem in tuple_list:
        for i in range(0,len(elem)):
            elem[i] = {"class": "File", "path": elem[i]}


    tuple_list_dict = {"tuple_list": tuple_list}
    with open(args["--out_metafile"], 'w') as p:
        json.dump(tuple_list_dict, p)

if __name__ == '__main__':
    main()

